// main.min.js - small initializer: icons, typed effect, preloader hide
(function(){
	function init(){
		// Feather icons (if available)
		try{ if(window.feather) window.feather.replace(); }catch(e){}

		// Typed.js hero effect
		try{
			if(window.Typed){
				new Typed('#typed',{ strings:["Student","Engineer","Space Technology","Teacher Assistant"], typeSpeed:60, backSpeed:40, backDelay:1500, loop:true });
			}
		}catch(e){}

		// Hide preloader after page load
		var pre = document.getElementById('preloader');
		if(pre){ window.addEventListener('load', function(){ pre.style.transition='opacity 300ms'; pre.style.opacity='0'; setTimeout(function(){ pre.style.display='none'; },350); }); }
	}

	if(document.readyState==='loading') document.addEventListener('DOMContentLoaded', init);
	else init();
})();
